mapper.update:
update systemmenu
set name = #{name},
  url = #{url},
  sn = #{sn},
  parent_id = #{parent.id}
where id = #{id}

mapper.select:
select id, name, url, sn, parent_id
from systemmenu
where id = #{id}

SELECT child.id, child.name, child.sn, child.parent_id, child.url, parent.id p_id, parent.name p_name from systemmenu child left join systemmenu parent on child.parent_id = parent.id
where child.parent_id is null
where child.parent_id = #{parentId}

  select id, name, url, sn, parent_id
from systemmenu
where id = #{id}
